import { motion, useTransform, useScroll } from "framer-motion";
import { useRef } from "react";

const Example = () => {
  return (
    <div style={{ backgroundColor: "#4B5563" }}>
      <div style={{ display: "flex", height: "48rem", alignItems: "center", justifyContent: "center" }}>
        <span style={{ fontWeight: "bold", textTransform: "uppercase", color: "#CBD5E0" }}>
          Scroll down
        </span>
      </div>
      <HorizontalScrollCarousel />
      <div style={{ display: "flex", height: "48rem", alignItems: "center", justifyContent: "center" }}>
        <span style={{ fontWeight: "bold", textTransform: "uppercase", color: "#CBD5E0" }}>
          Scroll up
        </span>
      </div>
    </div>
  );
};

const HorizontalScrollCarousel = () => {
  const targetRef = useRef(null);
  const { scrollYProgress } = useScroll({
    target: targetRef,
  });

  const x = useTransform(scrollYProgress, [0, 1], ["1%", "-95%"]);

  return (
    <section ref={targetRef} style={{ position: "relative", height: "300vh", backgroundColor: "#2D3748" }}>
      <div style={{ position: "sticky", top: 0, display: "flex", height: "100vh", alignItems: "center", overflow: "hidden" }}>
        <motion.div style={{ transform: `translateX(${x})`, display: "flex", gap: "1rem" }}>
          {cards.map((card) => {
            return <Card card={card} key={card.id} />;
          })}
        </motion.div>
      </div>
    </section>
  );
};

const Card = ({ card }) => {
  return (
    <div
      key={card.id}
      style={{
        position: "relative",
        height: "45rem",
        width: "45rem",
        overflow: "hidden",
        backgroundColor: "#EDF2F7",
      }}
    >
      <div
        style={{
          backgroundImage: `url(${card.url})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          position: "absolute",
          inset: 0,
          zIndex: 0,
          transition: "transform 300ms",
          ":hover": { transform: "scale(1.1)" }
        }}
      ></div>
      <div style={{ position: "absolute", inset: 0, zIndex: 10, display: "grid", placeItems: "center" }}>
        <p style={{ background: "linear-gradient(to bottom right, rgba(255, 255, 255, 0.2), rgba(255, 255, 255, 0))", padding: "2rem", fontSize: "6rem", fontWeight: "bold", textTransform: "uppercase", color: "#fff", backdropFilter: "blur(8px)" }}>
          {card.title}
        </p>
      </div>
    </div>
  );
};

export default Example;

const cards = [
  {
    url: "/imgs/abstract/1.jpg",
    title: "Title 1",
    id: 1,
  },
  {
    url: "/imgs/abstract/2.jpg",
    title: "Title 2",
    id: 2,
  },
  {
    url: "/imgs/abstract/3.jpg",
    title: "Title 3",
    id: 3,
  },
  {
    url: "/imgs/abstract/4.jpg",
    title: "Title 4",
    id: 4,
  },
  {
    url: "/imgs/abstract/5.jpg",
    title: "Title 5",
    id: 5,
  },
  {
    url: "/imgs/abstract/6.jpg",
    title: "Title 6",
    id: 6,
  },
  {
    url: "/imgs/abstract/7.jpg",
    title: "Title 7",
    id: 7,
  },
];
