import React, { useState } from "react";
import "./Timeline.css";

function Timeline() {
  const [expandedItems, setExpandedItems] = useState([]);

  const handleItemAction = (e) => {
    const { target } = e;
    const action = target?.getAttribute("data-action");
    const item = target?.getAttribute("data-item");

    if (action) {
      const isExpandAction = action === "expand";
      setExpandedItems(isExpandAction ? ["1", "2", "3", "4", "5", "6"] : []);
    } else if (item) {
      const isExpanded = expandedItems.includes(item);
      setExpandedItems(
        isExpanded
          ? expandedItems.filter((i) => i !== item)
          : [...expandedItems, item]
      );
    }
  };

  return (
    <div className="Timelinecontainer">
      <div className="LeftHalf">
        <div className="TimelineHeading">TIMELINE</div>
      </div>
      <div className="RightHalf" onClick={handleItemAction}>
        <div id="timeline" className="timeline">
          <div className="btn-group">
            <button className="btn" type="button" data-action="expand">
              Expand All
            </button>
            <button className="btn" type="button" data-action="collapse">
              Collapse All
            </button>
          </div>

          {/* Timeline Items */}
          {[1, 2, 3, 4, 5, 6].map((itemNumber) => (
            <div className="timeline__item" key={`item-${itemNumber}`}>
              <div className="timeline__item-header">
                <button
                  className="timeline__arrow"
                  type="button"
                  id={`item${itemNumber}`}
                  aria-labelledby={`item${itemNumber}-name`}
                  aria-expanded={expandedItems.includes(`${itemNumber}`)}
                  aria-controls={`item${itemNumber}-ctrld`}
                  aria-haspopup="true"
                  data-item={itemNumber}
                >
                  <svg
                    className="timeline__arrow-icon"
                    viewBox="0 0 24 24"
                    width="24px"
                    height="24px"
                  >
                    <use href="#arrow" />
                  </svg>
                </button>
                <span className="timeline__dot"></span>
                <span id={`item${itemNumber}-name`} className="timeline__meta">
                  <time className="timeline__date" dateTime="1970-01-01">
                    January 1, 1970
                  </time>
                  <br />
                  <strong className="timeline__title">
                    Event {itemNumber}
                  </strong>
                </span>
              </div>
              <div
                className={`timeline__item-body ${
                  expandedItems.includes(`${itemNumber}`)
                    ? "timeline__item-body--expanded"
                    : ""
                }`}
                id={`item${itemNumber}-ctrld`}
                role="region"
                aria-labelledby={`item${itemNumber}`}
                aria-hidden={!expandedItems.includes(`${itemNumber}`)}
              >
                <div className="timeline__item-body-content">
                  <p className="timeline__item-p">
                    This is the content for Event {itemNumber}.
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Timeline;
