import React from "react";
import "./Timeline.css";
import timelineImage from "./timelines.png"; // import the image
import { useState, useEffect } from "react";

function Timeline() {
  const [visibleEntries, setVisibleEntries] = useState(3);

  const handleScroll = () => {
    // Calculate the scroll position and update the visible entries accordingly
    const scrollPosition = window.scrollY || document.documentElement.scrollTop;
    const windowHeight = window.innerHeight;

    const totalEntries = document.querySelectorAll(
      ".timeline .relative"
    ).length;
    const entriesToShow = Math.min(
      totalEntries,
      Math.ceil((scrollPosition + windowHeight) / windowHeight)
    );

    setVisibleEntries(entriesToShow);
  };

  useEffect(() => {
    // Attach the scroll event listener
    window.addEventListener("scroll", handleScroll);

    // Remove the listener on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="Timelinecontainer">
      <div className="LeftHalf">
        <div className="TimelineHeading">TIMELINE</div>
      </div>
      <div className="RightHalf">
        <div className="timeline">
          {[...Array(visibleEntries)].map((_, index) => (
            <div className="relative" key={index}>
              <div className="dot"></div>
              <div className="pl-10">
                <span className="timeline-date" style={{ color: "#fff" }}>
                  4 March 2024
                </span>
                <h3 className="timeline-title">Registration Opens</h3>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Timeline;
